        AND create_time  >=  #{createTimeStart}


        AND create_time  <=  #{createTimeEnd}


        AND is_deleted=#{isDeleted}

mapper.update:
update t_hkp_hos_house


        open_flag=#{openFlag},


        is_deleted=#{isDeleted}


