mapper.select:
SELECT * FROM ( SELECT hos.house_code AS house_code, emp.user_name AS emp_name, u.user_name AS create_name, u.owner_dept_name AS create_dept_name, CASE updown_log.aft_status WHEN '1' THEN '' WHEN '2' THEN '' ELSE '' END AS up_down_type, updown_log.remark AS remark, updown_log.create_time AS create_time, hos.is_deleted as is_deleted FROM t_hkp_hos_updown_log updown_log LEFT JOIN t_hkp_hos_house hos ON updown_log.house_code = hos.house_code LEFT JOIN t_hkp_sys_user_user u ON updown_log.create_code = u.user_code LEFT JOIN t_hkp_sys_user_user emp ON hos.emp_code = emp.user_code ) up_down
ORDER BY ${orderBy}
${order}

SELECT h.house_code, h.house_name, h.house_number, h.create_time, h.where_layer, h.layer_area, h.min_acreage, h.area, to_char(round(h.price, 2),'999999990.99')||h.unit as price_str, ( -- case when l.city_name is null then '' else l.city_name end || -- case when l.region_name is null then '' else l.region_name end ||
case when l.street_name is null then '' else l.street_name end || case when l.community_name is null then '' else l.community_name end || case when l.detail_address is null then '' else l.detail_address end ) as ldetail_address, ow.houseowner_name, ow.houseowner_phone as owner_phone, u.user_name as emp_name FROM t_hkp_hos_house h LEFT JOIN t_hkp_hos_houseowner ow on h.owner_code=ow.houseowner_code LEFT JOIN t_hkp_sys_user_user u on h.emp_code=u.user_code left JOIN t_hkp_hos_location l on h.location_code=l.location_code where h.is_deleted=false
AND h.house_status = #{houseStatus}
and h.create_time >= #{startTime}
and h.create_time <= #{endTime}
AND h.create_dept_code IN (${_deptCodes})
AND h.create_dept_code IN
#{i}
order by h.create_time desc

SELECT * FROM ( SELECT hos.*, u.user_code as emp_code, u.user_name as emp_name, u.user_phone as emp_phone, l.user_name create_name, d.dept_code as owner_dept_code, d.dept_name as owner_dept_name FROM t_hkp_hos_house hos INNER JOIN t_hkp_sys_user_user u ON u.user_code = hos.emp_code INNER JOIN t_hkp_sys_user_user l ON l.user_code = hos.create_code INNER JOIN t_hkp_hrm_org_dept d ON d.dept_code = u.owner_dept_code ) TP where 1=1
AND is_deleted = #{isDeleted}
AND last_followup_time <= #{lastFollowupTime}
AND lease_expiration IS NOT NULL AND lease_expiration <= #{contractEndTimeEnd}
AND owner_dept_code IN (${_ownerDeptCodes})
AND owner_dept_code IN
#{i}
AND owner_dept_code = #{ownerDeptCode}
ORDER BY ${orderBy}
${order}

SELECT count(*) FROM ( SELECT hos.*, u.user_code as emp_code, u.user_name as emp_name, u.user_phone as emp_phone, l.user_name create_name, d.dept_code as owner_dept_code, d.dept_name as owner_dept_name FROM t_hkp_hos_house hos INNER JOIN t_hkp_sys_user_user u ON u.user_code = hos.emp_code INNER JOIN t_hkp_sys_user_user l ON l.user_code = hos.create_code INNER JOIN t_hkp_hrm_org_dept d ON d.dept_code = u.owner_dept_code ) TP where 1=1
AND is_deleted = #{isDeleted}
AND last_followup_time <= #{lastFollowupTime}
AND lease_expiration IS NOT NULL AND lease_expiration <= #{contractEndTimeEnd}
AND owner_dept_code IN (${_ownerDeptCodes})
AND owner_dept_code IN
#{i}
AND owner_dept_code = #{ownerDeptCode}

SELECT * FROM( SELECT h.*, l.location_alias as l_location_alias, l.province as l_province, l.city as l_city, l.region as l_region, l.street as l_street, l.province_name as l_province_name, l.city_name as l_city_name, l.region_name as l_region_name, l.street_name as l_street_name, l.community_name as l_community_name, l.detail_address as l_detail_address, e.user_name as emp_name, e.user_phone as emp_phone,
case when hf.house_code is null then false else true end as has_favorite,
o.houseowner_phone as owner_phone, o.houseowner_name as owner_name, o.houseowner_type as houseowner_type, o.company_name as company_name FROM t_hkp_hos_house h
INNER join t_hkp_hos_favorite f on f.house_code = h.house_code AND f.emp_code = #{favEmpCode}
INNER join t_hkp_union_cooperate c on c.house_code = h.house_code AND(c.apply_code = #{cooEmpCode} or c.receive_code = #{cooEmpCode})
left join t_hkp_hos_favorite hf on hf.house_code = h.house_code AND hf.emp_code = #{userCode}
LEFT JOIN t_hkp_hos_location l on l.location_code = h.location_code LEFT JOIN t_hkp_sys_user_user e on e.user_code = h.emp_code LEFT JOIN t_hkp_hos_houseowner o on o.houseowner_code = h.owner_code ) TP
AND ( (area >= #{area} and has_cut = #{hasCut}) or (area >= #{minArea} and area <= #{area}) )
AND area <= #{maxArea}
AND house_status = #{houseStatus}
AND house_purpose = #{housePurpose}
AND house_purpose IN (${housePurposes})
AND house_purpose IN
#{i}
AND house_type IN (${houseTypes})
AND l_street in (${cusStreet})
AND l_region in (${cusRegion}) AND l_street not in (${cusStreet})
AND l_city in (${cusCity}) AND l_region not in (${cusRegion}) AND l_street not in (${cusStreet})
AND emp_code in (${userCodes})
ORDER BY ${orderBy}
${order}

